import React, { Component } from 'react'
import Config from '../../Utils/Config';
import { FiGrid } from "react-icons/fi";
import './index.scss';
import { NavLink } from 'react-router-dom';

export class Sidebar extends Component {  

    render() {
        return (
            <React.Fragment>
            <div className={this.props.isActive ? "sidebar open" : "sidebar" }>
                <div className="sidebar-logo mb-4">
                    <img src={Config.logo} alt="" />
                </div>

                <ul className="sidebar-menu list-unstyled">
                    {Menu.map((link, idx) => (
                    <li key={idx}>
                        <NavLink exact to={link.url} activeClassName="active">
                        <FiGrid className="me-2"/>{link.title}
                        </NavLink>
                    </li>
                    ))}
                </ul>
            </div>
            {this.props.isActive? <span className="sidebar-overlay" onClick={this.props.handleClick}></span>:null}
            </React.Fragment>
        )
    }
}

export default Sidebar;

