import React, { Component } from 'react'
import Config from '../../Utils/Config';
import { FiBarChart2, FiDollarSign, FiGrid, FiPower, FiSettings, FiUser } from "react-icons/fi";
import './index.scss';
import { NavLink } from 'react-router-dom';

export class Sidebar extends Component {  

    render({isActive} = this.props) {
        return (
            <React.Fragment>
            <div className={isActive ? "sidebar open" : "sidebar" }>
                <div className="sidebar-logo mb-4">
                    <img src={Config.logo} alt="" />
                </div>

                <ul className="sidebar-menu list-unstyled">
                    {Menu.map((link, idx) => (
                    <li key={idx}>
                        <NavLink exact to={link.url} activeClassName="active">
                            <span className="d-inline-flex me-2">
                                {link.icon}
                            </span>
                            {link.title}
                        </NavLink>
                    </li>
                    ))}
                </ul>
            </div>
            {isActive? <span className="sidebar-overlay" onClick={this.props.handleClick}></span>:null}
            </React.Fragment>
        )
    }
}

export default Sidebar;

const Menu = [
    {
        id: 1,
        icon: <FiGrid />,
        title: "Dashboard",
        url: "/dashboard"
    },
    {
        id: 2,
        icon: <FiBarChart2 />,
        title: "Reports",
        url: "/products"
    },
    {
        id: 3,
        icon: <FiDollarSign />,
        title: "Earnings",
        url: "/earnings"
    },
    {
        id: 4,
        icon: <FiUser />,
        title: "Skills",
        url: "/skills"
    },
    {
        id: 4,
        icon: <FiSettings />,
        title: "Settings",
        url: "/settings"
    },
    {
        id: 5,
        icon: <FiPower />,
        title: "Logout",
        url: "/logout"
    },
]
