import React from 'react'

class Select extends React.Component{
    static propTypes = {
      options: React.PropTypes.array
    };

    constructor(props){
      super(props);
      this.onOpen = this.onOpen.bind(this);
      this.onSelect = this.onSelect.bind(this);
      this.state = {
        selected: null,
        opened: false
      };
    }

    onOpen(){
      this.setState({
        opened: !this.state.opened
      });
    }

    onSelect(option){
      this.setState({
        selected: option,
        opened: false
      }, ()=> {
        //console.info(this.state);
      });
    }

    getOptions(){
      return this.props.options.map( o => <Option key={o.key} option={o} onSelect={this.onSelect} />);
    }

    render(){
      let items = this.getOptions();
      let selected = this.state.selected ? 
        this.state.selected.value : 'Select';
      let cssClass = this.state.opened ? 'show': 'hide';

      return (
        <div className="select" onClick={this.onOpen}>
          <span>
            {selected}
          </span>
          <ul className={cssClass}>{items}</ul>
        </div>
      );
    }
  }

  class Option extends React.Component{
    static propTypes = {
      option: React.PropTypes.object,
      onSelect: React.PropTypes.func
    };

    constructor(props){
      super(props);
      this.onSelect = this.onSelect.bind(this);
    }

    onSelect(e){
      e.preventDefault();
      this.props.onSelect(this.props.option);
    }

    render(){
      return (
        <li><a href="#" onClick={this.onSelect}>{this.props.option.value}</a></li>
      );
    }
  }

  const App = props => {
    let select1 = [
        { key: "o1", value: "Option 1" },
        { key: "o2", value: "Option 2" },
        { key: "o3", value: "Option 3" }
      ];

    let select2 = [
        { key: "o1", value: "Option 1" },
        { key: "o2", value: "Option 2" },
        { key: "o3", value: "Option 3" },
        { key: "o4", value: "Option 4" },
        { key: "o5", value: "Option 5" }
      ];

    return ( 
      <div>
        Example here: <Select options={select1} /> and here: <Select options={select2} />
      </div>
    );
  }


export default Select
